// --- State ---
let state = {
    todos: [],
    appName: '',
    viewMode: 'Daily',
    selectedCategory: 'Essence',
    language: 'en',
    timer: { active: false, timeLeft: 1500, taskId: null, interval: null },
    athkarCounts: {},
    tasbihCounts: {},
    athkarMode: 'morning',
    ibadah: { kahf: [], athkar: {}, tasbih: {} },
    theme: 'dark'
};

// --- Initialization ---
document.addEventListener('DOMContentLoaded', () => {
    // 1. Load Data
    loadLocalData();

    // 2. Setup Events
    setupEventListeners();

    // 3. Set Language (Renders UI)
    const storedLang = localStorage.getItem('beltagy_lang');
    setLanguage(storedLang || 'en');

    // 4. Set Theme
    const storedTheme = localStorage.getItem('beltagy_theme');
    setTheme(storedTheme || 'dark');

    // 5. Update UI
    updateStats();
    initCategoryDock(); // Ensure categories render regardless of language/icon loading
});

function loadLocalData() {
    try {
        const todosRaw = localStorage.getItem('beltagy_todos');
        state.todos = todosRaw ? JSON.parse(todosRaw) : [];
    } catch (e) {
        console.error('Corrupt todos', e);
        state.todos = [];
    }

    try {
        const prefsRaw = localStorage.getItem('beltagy_prefs');
        const prefs = prefsRaw ? JSON.parse(prefsRaw) : {};
        if (prefs.appName) state.appName = prefs.appName;
        if (prefs.selectedCategory) state.selectedCategory = prefs.selectedCategory;
    } catch (e) { console.error('Corrupt prefs', e); }

    document.getElementById('app-name-input').value = state.appName;

    try {
        const ibadahRaw = localStorage.getItem('beltagy_ibadah');
        state.ibadah = ibadahRaw ? JSON.parse(ibadahRaw) : { kahf: [], athkar: {}, tasbih: {} };
    } catch (e) {
        state.ibadah = { kahf: [], athkar: {}, tasbih: {} };
    }

    try {
        const countsRaw = localStorage.getItem('beltagy_counts');
        const counts = countsRaw ? JSON.parse(countsRaw) : {};
        state.athkarCounts = counts.athkar || {};
        state.tasbihCounts = counts.tasbih || {};
    } catch (e) {
        state.athkarCounts = {};
        state.tasbihCounts = {};
    }

    // Migration: Essence (Personal + Ideas)
    let migrated = false;
    state.todos.forEach(t => {
        if (t.category === 'Personal' || t.category === 'Ideas') {
            t.category = 'Essence';
            migrated = true;
        }
    });
    if (state.selectedCategory === 'Personal' || state.selectedCategory === 'Ideas') {
        state.selectedCategory = 'Essence';
        migrated = true;
    }
    if (migrated) saveLocalData();
}

function saveLocalData() {
    localStorage.setItem('beltagy_todos', JSON.stringify(state.todos));
    localStorage.setItem('beltagy_prefs', JSON.stringify({
        appName: state.appName,
        selectedCategory: state.selectedCategory
    }));
    localStorage.setItem('beltagy_ibadah', JSON.stringify(state.ibadah));
    localStorage.setItem('beltagy_counts', JSON.stringify({
        athkar: state.athkarCounts,
        tasbih: state.tasbihCounts
    }));
}

// --- Window Functions ---
window.addTodo = async () => {
    const input = document.getElementById('todo-input');
    const text = input.value.trim();
    if (!text) return;

    // Default to 'Essence' if 'All' is selected, otherwise use selected category
    const category = state.selectedCategory === 'All' ? 'Essence' : state.selectedCategory;
    let colorClass = CATEGORIES[category].color;

    state.todos.unshift({
        id: Date.now().toString(),
        text,
        completed: false,
        category: category,
        colorClass,
        viewMode: state.viewMode,
        createdAt: new Date()
    });
    saveLocalData();
    input.value = '';
    render();
    updateStats();
};

window.toggleTodo = async (id) => {
    const todo = state.todos.find(t => t.id === id);
    if (todo) {
        const wasCompleted = todo.completed;
        todo.completed = !todo.completed;
        saveLocalData();
        render();
        updateStats();
    }
};

window.deleteTodo = async (id) => {
    state.todos = state.todos.filter(t => t.id !== id);
    saveLocalData();
    render();
    updateStats();
};

window.switchView = (mode) => {
    state.viewMode = mode;
    const VIEW_COLORS = {
        'Daily': 'bg-indigo-500 text-white shadow-lg shadow-indigo-500/20',
        'Weekly': 'bg-indigo-500 text-white shadow-lg shadow-indigo-500/20',
        'Athkar': 'bg-sky-500 text-white shadow-lg shadow-sky-500/20',
        'Tasbih': 'bg-emerald-600 text-white shadow-lg shadow-emerald-500/20',
        'Kahf': 'bg-amber-500 text-white shadow-lg shadow-amber-500/20'
    };
    ['Daily', 'Weekly', 'Athkar', 'Tasbih', 'Kahf'].forEach(v => {
        const btn = document.getElementById(`btn-${v.toLowerCase()}`);
        if (btn) {
            btn.className = v === mode
                ? `px-5 py-1.5 rounded-lg text-xs font-medium transition-all ${VIEW_COLORS[v] || VIEW_COLORS['Daily']}`
                : `px-5 py-1.5 rounded-lg text-xs font-medium transition-all text-slate-400 hover:text-white`;
        }
    });
    render();
};

window.handleLogin = () => { }; // No-op for local version
window.handleRegister = () => { }; // No-op
window.handleGuestLogin = () => { }; // No-op
window.logout = () => { }; // No-op

window.toggleTheme = () => {
    setTheme(state.theme === 'dark' ? 'light' : 'dark');
};

function setTheme(mode) {
    state.theme = mode;
    localStorage.setItem('beltagy_theme', mode);
    if (mode === 'dark') document.documentElement.classList.add('dark');
    else document.documentElement.classList.remove('dark');
}

function setLanguage(lang) {
    if (!TRANSLATIONS[lang]) lang = 'en';
    state.language = lang;
    localStorage.setItem('beltagy_lang', lang);
    const t = TRANSLATIONS[lang];
    const isRTL = lang === 'ar';
    document.documentElement.setAttribute('dir', isRTL ? 'rtl' : 'ltr');
    // ... Minimal translations for brevity, full logic in render ...
    // Dashboard / Stats Localization
    const elements = {
        'tab-btn-tasks': t.tabTasks,
        'tab-btn-worship': t.tabWorship,
        'header-kahf-tracker': `<i data-lucide="book-open" class="w-5 h-5"></i> ${t.kahfTracker}`,
        'header-tasbih-activity': `<i data-lucide="bar-chart-2" class="w-5 h-5"></i> ${t.tasbihActivity}`,
        'btn-daily': t.daily,
        'btn-weekly': t.weekly,
        'btn-athkar': t.athkar,
        'btn-tasbih': t.tasbih,
        'btn-kahf': t.kahf
    };
    Object.entries(elements).forEach(([id, html]) => {
        const el = document.getElementById(id);
        if (el) el.innerHTML = html;
    });
    if (window.lucide) lucide.createIcons();

    render();
    initCategoryDock();
}

function setCategory(cat) {
    state.selectedCategory = cat;
    initCategoryDock();
    saveLocalData();
    render();
}

function render() {
    const list = document.getElementById('todo-list');
    list.innerHTML = '';
    const t = TRANSLATIONS[state.language];

    if (state.viewMode === 'Athkar') { renderAthkar(list); return; }
    if (state.viewMode === 'Tasbih') { renderTasbih(list); return; }
    if (state.viewMode === 'Kahf') { renderKahf(list); return; }

    // Filter by View Mode (Daily/Weekly)
    let filtered = state.todos.filter(t => t.viewMode === state.viewMode);

    // Filter by Category (if not 'All')
    if (state.selectedCategory !== 'All') {
        filtered = filtered.filter(t => t.category === state.selectedCategory);
    }

    if (filtered.length === 0) {
        document.getElementById('empty-state').classList.remove('hidden');
        document.getElementById('empty-state').classList.add('flex');
    } else {
        document.getElementById('empty-state').classList.add('hidden');
        document.getElementById('empty-state').classList.remove('flex');

        const CATEGORY_STYLES = {
            'Essence': 'bg-gradient-to-r from-rose-200 to-amber-200 border-l-4 border-l-rose-500 border-y border-r border-rose-300 dark:border-rose-500/30 shadow-lg shadow-rose-500/20',
            'Work': 'bg-gradient-to-r from-sky-200 to-blue-200 border-l-4 border-l-blue-600 border-y border-r border-blue-300 dark:border-blue-500/30 shadow-lg shadow-blue-500/20',
            'Purchases': 'bg-gradient-to-r from-teal-200 to-emerald-200 border-l-4 border-l-teal-600 border-y border-r border-teal-300 dark:border-teal-500/30 shadow-lg shadow-teal-500/20',
            'Health': 'bg-gradient-to-r from-green-200 to-lime-200 border-l-4 border-l-green-600 border-y border-r border-green-300 dark:border-green-500/30 shadow-lg shadow-green-500/20',
            'Default': 'bg-gradient-to-r from-slate-200 to-gray-200 border-l-4 border-l-slate-500 border-y border-r border-slate-300 dark:border-slate-500/30 shadow-lg shadow-slate-500/20'
        };

        const activeTasks = filtered.filter(t => !t.completed);
        const completedTasks = filtered.filter(t => t.completed);

        const renderTask = (todo) => {
            const el = document.createElement('div');
            const lightStyle = CATEGORY_STYLES[todo.category] || CATEGORY_STYLES['Default'];
            // Dark Mode Fix: dark:bg-none removes the light gradient, dark:bg-slate-800/50 provides the dark backing
            // Border colors are now handled by lightStyle (CATEGORY_STYLES)
            el.className = `group relative ${lightStyle} dark:bg-none dark:bg-slate-800/50 hover:shadow-lg dark:hover:bg-slate-700/50 rounded-2xl p-4 transition-all duration-300 ${todo.completed ? 'opacity-60 grayscale' : ''}`;

            el.innerHTML = `
                <div class="flex items-center gap-4">
                    <button onclick="toggleTodo('${todo.id}')" 
                        class="w-6 h-6 rounded-full border-2 flex items-center justify-center transition-all duration-300 ${todo.completed
                    ? 'bg-emerald-500 border-emerald-500'
                    : 'border-slate-400 hover:border-slate-600 dark:border-slate-500 dark:hover:border-white'
                }">
                        ${todo.completed ? '<i data-lucide="check" class="w-3 h-3 text-white"></i>' : ''}
                    </button>
                    
                    <div class="flex-1 min-w-0">
                        <p class="text-lg font-bold break-words leading-relaxed ${todo.completed ? 'text-slate-600 dark:text-slate-500 line-through decoration-slate-400' : 'text-slate-900 dark:text-slate-100'}">
                            ${todo.text}
                        </p>
                        <p class="text-[12px] text-slate-600 dark:text-slate-400 flex items-center gap-2 mt-1 font-bold opacity-80">
                            <span class="text-sm">${(CATEGORIES[todo.category] && CATEGORIES[todo.category].icon) || '•'}</span>
                            ${t[todo.category] || todo.category}
                        </p>
                    </div>

                    <div class="flex items-center gap-1 opacity-100">
                         ${!todo.completed ? `<button onclick="openTimer('${todo.id}')" class="p-2 text-slate-500 dark:text-slate-400 hover:text-indigo-500 hover:bg-indigo-500/10 rounded-lg transition-colors" title="Focus Mode">
                            <i data-lucide="play" class="w-5 h-5 rtl-flip"></i> 
                        </button>` : ''}
                        <button onclick="deleteTodo('${todo.id}')" class="p-2 text-slate-500 dark:text-slate-400 hover:text-red-500 hover:bg-red-500/10 rounded-lg transition-colors">
                            <i data-lucide="trash-2" class="w-5 h-5"></i>
                        </button>
                    </div>
                </div>
            `;
            return el;
        };

        // Render Active Tasks (Focus Zone)
        if (activeTasks.length > 0) {
            const container = document.createElement('div');
            container.className = "mb-8 space-y-3"; // Vertical spacing

            const header = document.createElement('h3');
            header.className = "text-sm font-bold text-indigo-500 dark:text-indigo-400 uppercase tracking-widest mb-4 px-2 flex items-center gap-2";
            header.innerHTML = `<span class="w-2 h-2 rounded-full bg-indigo-500 animate-pulse"></span> ${t.tabTasks || "Active Tasks"} <span class="text-xs bg-indigo-100 dark:bg-indigo-900/30 text-indigo-600 dark:text-indigo-300 px-2 py-0.5 rounded-full ml-auto">${activeTasks.length}</span>`;

            list.appendChild(header);
            activeTasks.forEach(t => container.appendChild(renderTask(t)));
            list.appendChild(container);
        }

        // Render Completed Tasks (Secondary Zone)
        if (completedTasks.length > 0) {
            const container = document.createElement('div');
            container.className = "space-y-3 opacity-90";

            const header = document.createElement('h3');
            header.className = "text-sm font-bold text-slate-400 dark:text-slate-500 uppercase tracking-widest mb-4 px-2 border-t border-slate-200 dark:border-white/5 pt-6 flex items-center gap-2";
            header.innerHTML = `${t.tasksDone || "Completed"}`;

            list.appendChild(header);
            completedTasks.forEach(t => container.appendChild(renderTask(t)));
            list.appendChild(container);
        }

        if (window.lucide) lucide.createIcons();
    }
}

