function initAmbientVisuals() {
    const container = document.getElementById('emoji-container');
    if (!container) return;
    container.innerHTML = '';

    // Abstract, sophisticated shapes
    const SHAPES = ['hexagon', 'circle', 'triangle', 'diamond', 'sparkles', 'zap', 'star'];

    for (let i = 0; i < 20; i++) {
        const el = document.createElement('div');
        const shape = SHAPES[Math.floor(Math.random() * SHAPES.length)];

        // Use Lucide icons for modern look
        el.innerHTML = `<i data-lucide="${shape}" class="w-full h-full"></i>`;

        // Random properties for organic flow
        const size = 30 + Math.random() * 50;
        const duration = 20 + Math.random() * 20;
        const delay = Math.random() * 10;
        const left = Math.random() * 100;

        el.className = 'absolute text-indigo-500/10 dark:text-white/5 pointer-events-none filter blur-[1px] mix-blend-overlay';
        el.style.left = `${left}%`;
        el.style.bottom = '-10%';
        el.style.width = `${size}px`;
        el.style.height = `${size}px`;
        el.style.animation = `float ${duration}s infinite linear, spin ${duration * 2}s infinite linear`;
        el.style.animationDelay = `-${delay}s`;

        container.appendChild(el);
    }
    lucide.createIcons();
}

function initCategoryDock() {
    const dock = document.getElementById('category-dock');
    if (!dock) return;
    dock.innerHTML = '';
    const t = TRANSLATIONS[state.language];

    // Category List including 'All'
    const categories = ['All', ...Object.keys(CATEGORIES)];

    // Safety
    if (!categories.includes(state.selectedCategory)) state.selectedCategory = 'All';

    categories.forEach(cat => {
        const btn = document.createElement('button');
        const isActive = state.selectedCategory === cat;

        const baseClass = "px-4 py-2 rounded-full text-sm font-bold transition-all flex items-center gap-2 z-10 relative";

        let activeColor = 'bg-slate-600'; // Default for 'All'
        let shadowColor = 'shadow-slate-500';
        let icon = '🌐'; // Default icon for All
        let label = cat === 'All' ? (t.all || 'All') : (t[cat] || cat);

        if (cat !== 'All') {
            activeColor = CATEGORIES[cat].bg;
            shadowColor = activeColor.replace('bg-', 'shadow-');
            icon = CATEGORIES[cat].icon || '•';
        }

        const activeClass = isActive
            ? `${activeColor} text-white shadow-lg ${shadowColor}/30 scale-105 border border-transparent`
            : `bg-white/50 dark:bg-slate-800/50 text-slate-900 dark:text-white hover:bg-white dark:hover:bg-slate-700 border border-slate-200 dark:border-slate-700`;

        btn.className = `${baseClass} ${activeClass}`;

        btn.innerHTML = `<span class="text-lg leading-none filter drop-shadow-sm">${icon}</span> ${label}`;
        btn.onclick = () => setCategory(cat);
        dock.appendChild(btn);
    });

    // Flex wrap instead of scroll for categories
    dock.className = "flex items-center gap-3 flex-wrap justify-center pb-4 px-2 mx-auto w-full max-w-2xl";
}
